const themeToggle=document.getElementById('themeToggle');const body=document.body;let localStorageThemeKey='otbr-theme';const url=window.location.pathname;if(url.substr(0,7)==='/index.'){localStorageThemeKey='otbr-theme-index-html'}if(url.substr(0,5)==='/ota.'){localStorageThemeKey='otbr-theme-ota-html'}const savedTheme=localStorage.getItem(localStorageThemeKey);if(savedTheme==='dark'){body.classList.add('dark-theme');document.documentElement.setAttribute('data-bs-theme','dark')}else{body.classList.remove('dark-theme');document.documentElement.setAttribute('data-bs-theme','light')}themeToggle.addEventListener('click',function(){body.classList.toggle('dark-theme');const isDark=body.classList.contains('dark-theme');const newTheme=isDark?'dark':'light';localStorage.setItem(localStorageThemeKey,newTheme);document.documentElement.setAttribute('data-bs-theme',newTheme)});themeToggle.addEventListener('keypress',function(e){if(e.key==='Enter'||e.key===' '){e.preventDefault();this.click()}});themeToggle.setAttribute('tabindex','0');themeToggle.setAttribute('role','button');themeToggle.setAttribute('aria-label','Switch theme');